  sprintf(str1, "%-20f", 100000.4);
  s21_sprintf(str2, "%-20f", 100000.4);
  ck_assert_str_eq(str1, str2);
  makefreeArr(str1, str2, 100);

  sprintf(str1, "%-+20f", 100000.4);
  s21_sprintf(str2, "%-+20f", 100000.4);
  ck_assert_str_eq(str1, str2);
  makefreeArr(str1, str2, 100);

  sprintf(str1, "%-20f", -100000.4);
  s21_sprintf(str2, "%-20f", -100000.4);
  ck_assert_str_eq(str1, str2);
  makefreeArr(str1, str2, 100);

  sprintf(str1, "%+20f", 100000.4);
  s21_sprintf(str2, "%+20f", 100000.4);
  ck_assert_str_eq(str1, str2);
  makefreeArr(str1, str2, 100);

  sprintf(str1, "%+20f", -100000.4);
  s21_sprintf(str2, "%+20f", -100000.4);
