  sprintf(str1, "%+10d%%\n", 4);
  s21_sprintf(str2, "%+10d%%\n", 4);
  ck_assert_str_eq(str1, str2);
  makefreeArr(str1, str2, 30);

  sprintf(str1, "%+10d%%\n", -4);
